float hexx( in vec2 p, in float b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(d.y,0.0);
}
float hexy( in vec2 p, in float b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(d.x,0.0);
}
float sdCircle( in vec2 p, in float r ) 
{
    return length(p)-r;
}
vec3 turquoise_blue=vec3(0.0,0.68,0.8);
vec3 wine_red=vec3(0.57,0.18,0.26);
vec3 warm_beige=vec3(0.9,0.78,0.65);
vec3 red_apple=vec3(0.88,0.32,0.41);
vec3 medium_gray=vec3(0.623,0.627,0.627);  
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    vec3 col=medium_gray;
    float d,d2;
    float AA=3.0/min(iResolution.x, iResolution.y);
    float x=0.0;
    for(float x=-5.0;x<5.0;x++)
    {
        for(float y=-2.0;y<5.0;y++)
        {
            d=hexy(p+vec2(x,-y)*0.45,0.2);
            d2=sdCircle(p+vec2(x,y-2.)*0.45,0.04);
            col=(d>0.0) ? col:turquoise_blue;
            col=(d2>0.0) ? col:warm_beige;
            col = mix( col, vec3(0.0),smoothstep(AA,0.0,abs(d))+smoothstep(AA,0.0,abs(d2)));
        }
    }
    fragColor = vec4(col,1.0);
}
