//fork of https://www.shadertoy.com/view/DljyWG by Xor
//there was cool stuff hidden behind the sphere

//i might like this modified version more:
void mainImage(out vec4 O, vec2 I)
{
    vec3  p,q,r=iResolution;
    float i=2.,j,z;  //modified
    for(O *= z;  i>0.;  i-=.06 )
        z = p.z = sqrt( max( z = i - dot( p = vec3(I+I-r.xy,0)/r.y, p ) *3., -z/1e5 )  ), //modified
        p /= 2. + z,
        p.xz *= mat2(cos(iTime/2.+vec4(0,11,33,0))),
        O += i * pow(z,.15) 
               * pow( cos( j = cos(j) * dot( cos(q+=p), sin(q.yzx) ) /.3 ) +1. , 8. )
               * ( sin(i*3e1 +vec4(0,1,2,3)) +1. ) / 1.5e3;

    O = 1. - exp(-O);  //added
    O*=O;
}

