vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return
        abs(fract((a1)*(a2.x+a2.y))-.5)
    ;
}
vec2 uv3(vec2 uv){
    if(uv.x>uv.y) return uv.yx;
    else return
        uv
        //abs(uv-.5)
    ;
}

vec3 fractal(vec2 uv){
    vec3 col = vec3(0.);
    vec2 t2 = vec2(0.);
    for(int k = 0; k < 6; k++){
        //if(k % 2 == 0) uv *= 1.5;
        uv =
            uv3(abs(.5+uv+t2)/1.5)
        ;
        //uv = -abs(fract(vec2(uv+vec2(.5,1.5))*3.)-.5)/3.;
        //if(uv.y<uv.x) uv = uv.yx-vec2(.5,1.5); else uv *= 1.5; if((k)%3 == 0) uv -= vec2(.5,1.5); 

        t2 =
            -triangle_wave(-uv-.5).yx
        ;
        uv =
            t2-triangle_wave(-uv.yx).yx
        ;

        col =
            vec3(col.zx,max(abs(uv.x-uv.y)*sign(t2.x-t2.y),0.))
        ;
        }
        return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    fragColor = vec4(0.0);
    float t1 = 1.;
    vec2 draggedPos = texelFetch(iChannel0, ivec2(1,0), 0).xy;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    uv -= draggedPos/iResolution.y/t1/2.0;
    vec3 col = fractal(uv);
    fragColor = vec4(col*2.,1.0);
}

//from https://www.shadertoy.com/view/dtGGzK

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 col = vec4(0);
    if (floor(fragCoord) == vec2(1,0)) {
        vec2 prevpos = texelFetch(iChannel0, ivec2(1,0), 0).zw; //previous dragged mouse pos
        vec4 mp = texelFetch(iChannel0, ivec2(2,0), 0); //prev mouse pos
        vec4 m = iMouse; //current mouse pos

        //if mouse button up
        if (m.z < 0.0 && mp.z >= 0.0){
            col = texelFetch(iChannel0, ivec2(1,0), 0).xyxy;
        } else if (m.z >= 0.0){
            col = vec4(m.xy - abs(m.zw) + prevpos, prevpos);
        } else {
            col = texelFetch(iChannel0, ivec2(1,0), 0);
        }
    }
    //the last mouse position
    if (floor(fragCoord) == vec2(2,0)) {
        col = iMouse;
    }
    fragColor = col;
}
