void mainImage(out vec4 Z, in vec2 A)
{
    vec2 B = (A * 2.0 - iResolution.xy) / iResolution.y;
    float C = sin(length(B) * 4.0 - iTime) * -0.05;
    B += B * C;
    float L = length(B);
    float W = 50.*(1.+ C)/iResolution.y;
    float H = 50.*(1.+ C)/iResolution.x;
    float D = smoothstep(W * 2.5, H * 10., abs(fract(L * 20.) - 0.25) - 0.25);
    vec2 uv = A/iResolution.xy;
    vec3 E = vec3(D);
    vec3 F = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 G = E * F;
    Z = vec4(G, 1.0);
}
