/**/ // --- 291 chars    see golfed version below

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = ( u+u - R ) / R.y;
    float a = .393,                   // 2pi/16
          l = length(U); 
    U = l * abs(cos( mod( atan(U.y,U.x)+a, a+a ) -a + vec2(0,11) )); // radial symmetry + mirror
    a = min( min( U.y,                                               // rays
                  abs(l-.2) ),                                       // central disk
             min( abs(length(U-vec2(.8,0)) -.1),                     // outer C
                  U.y < .1 && abs(U.x-.5) < .15                      // 3 strips
                    ? abs( mod(U.x+.05,.1) - .05 ) : 9. 
           )    );
    O = vec4( l < .8 ? R.y/3.*(a-.01) : 1. ) ;                       // AA draw
}





