const float pos_x = -0.61;
const float pos_y = 0.23;
const float t = 4.60;

float cloudShape(vec2 uv) {
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    float cloudBody1 = smoothstep(0.0, 0.2, length(uv * vec2(1.0, 2.0)) - 0.2);
    float cloudBody2 = smoothstep(0.1, 0.2, uv.y - sin(uv.x * 3.0) * 0.1);
    float cloudBody3 = smoothstep(0.0, 0.2, length(uv - vec2(-0.1, -0.35)) - 0.1);
    return min(min(cloudBody1, cloudBody2), cloudBody3);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 bgColor = vec3(0.529, 0.808, 0.922);
    vec3 cloudColor = vec3(0.3, 0.3, 1.0);
    vec2 pos = vec2(pos_x, pos_y);
    float time = t + iTime * 0.5;
    pos.x += sin(time) * 0.2;
    pos.y += cos(time * 0.5) * 0.1;
    vec3 color = mix(bgColor, cloudColor, cloudShape(uv + pos)); 
    fragColor = vec4(color, 1.0);
}
