float rand(vec2 n) {return fract(sin(dot(n,vec2(12.9898,12.1414)))*83758.5453);}
float noise(vec2 n) {
    const vec2 d = vec2(0.0, 1.0);
    vec2 b = floor(n);
    vec2 f = vec2(smoothstep(0., 1., fract(n)));
    return mix(mix(rand(b), rand(b + d.yx), f.x), mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y);
}
float fire(vec2 n) {return noise(n) + noise(n * 2.3)*.31 + noise(n * 5.7)*.41;}

vec2 polar(vec2 uv, float shift) {
    uv = vec2(0.5)-uv;
    float px = atan(uv.y, uv.x) + shift;
    float py = length(uv);
    return vec2(px, py);
}

float shade(vec2 uv, float t) {
    uv.y *= -0.1;
    uv.x *= 5.3;
    float q = fire(uv - t);
    vec2 r = vec2(fire(uv - q - t - uv.x - uv.y), fire(uv + q + t));
    return 2.-r.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float t = -2.*iTime;
    vec2 uv = fragCoord/iResolution.y;
    float ff = uv.y;
    uv.x -= (iResolution.x / iResolution.y - 1.0) / 2.0;
    vec2 uv2 = uv;
    uv2.y = 1.0 - uv2.y;
   	uv = polar(uv, 1.3);

    float c = shade(uv, t);

    uv = fragCoord/iResolution.xy;
    float d = length(uv - 0.5);

    //mouse control
    float m = 3. - iMouse.x / iResolution.x * 2.;

    c = pow(c*d*d,m);

    fragColor = vec4(0.)+c;
}
