#define PI 3.1415926538
#define N 3.0 // 4 and 6 are also interesting

//[[0.788 0.388 1.458] [0.978 0.648 1.598] [-0.392 0.228 -0.392] [1.428 3.686 0.608]]
//[[0.979 0.633 0.776] [0.419 0.590 0.419] [0.632 0.529 0.853] [5.304 5.969 3.875]]
vec3 palette( float t ) {
    vec3 a = vec3(0.979, 0.633, 0.776);
    vec3 b = vec3(0.419, 0.590, 0.419);
    vec3 c = vec3(0.632, 0.529, 0.853);
    vec3 d = vec3(5.304, 5.969, 3.875);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float theta = atan(uv.x, uv.y);
    float r = 2.0 * PI / N;
    // dist from centre, for large triagle, moving up and down
    float dist = 1.0 - cos(floor(0.5 + theta / r) * r - theta) * length(uv + vec2(0.0, sin(iTime * .5)));

    uv = fract(uv * 2.2) - .5;
    theta = atan(uv.x, uv.y);
    vec3 col = palette(dist);
    // relative distance
    float d = 1.0 - cos(floor(0.5 + theta / r) * r - theta) * length(uv);
    d = sin(d*16. + iTime*4.0)/16.;
    d = abs(d);
    d = pow(0.02 / d, 1.1);
    col = smoothstep(.9,.95, col*d);
    fragColor = vec4(col, 1.0);
}
