const vec2 s = vec2(1, 1.7320508);
const float border = .025;
const float speed = 1.;
const float nb = 30.;
const float exponant = 1.6;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;
    float t = iTime;

    uv = abs(uv);

    float m =  max(dot(uv, s*.5), uv.x);

    m *= exp(-length(uv)*exponant);
    m = sin(m*nb + t*speed)/nb;
    m = abs(m);
    m = step(border,m);

    vec3 finalCol = vec3(m);
    fragColor = vec4(finalCol,1.0);
}
