void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*5./iResolution.xy)-2.5;


    float cz = length(uv);

    vec3 col = vec3(0., 1., .005);
    uv *= fract(uv.xy)*32.;
    col = normalize(col);
    col *= vec3(.04, .04, .04);
    float d = sin(cz*0.5-(iTime*1.04)*2.)*(uv.x*.9)* 1.;
    float e = sin(cz*0.5-(iTime*1.04)*2.)*(uv.y*.9)* 1.;
    d = abs(d);
    d = 0.02/d;
    e = abs(e);
    e = 0.02/e;
    col *= d + e; 



    // Output to screen
    fragColor = vec4(col,1.);
}
