#define PI 3.141592653
#define COUNT 20.0

float line(vec2 st, float width)
{
    return smoothstep(width, 0.0, abs(st.y - st.x));
}

float drawCoord(vec2 uv)
{
   float x = distance(vec2(uv.x, 0.0), vec2(0.0, 0.0));
   float y = distance(vec2(0.0, uv.y), vec2(0.0, 0.0));

   return 1e-3 / x + 1e-3 / y;
}

float sphere(vec2 coord, vec2 p, float radius)
{
    float d = distance(p, coord);
    d = abs(d - radius);
    return 1e-3 / d;
}

float sphereShape(vec2 coord, vec2 p, float radius)
{
    float d = distance(p, coord);
    d /= radius;
    d = 1.0 - d;
    d = clamp(d * 24.0, 0.0, 1.0);
    return d;
}

float net(vec2 coord)
{
    float xline = cos(coord.x * PI * COUNT);
    xline = step(1.0 - 1e-1, xline);

    float yline = cos(coord.y * PI * COUNT);
    yline = step(1.0 - 1e-1, yline);

    return xline + yline;
}

float QuadrantIV(vec2 coord, float warp)
{
    float f = step(0.0, -coord.x);
    f *= step(0.0, coord.y);
    return abs(warp - f);
}

float QuadrantII(vec2 coord, float warp)
{
    float f = step(0.0, coord.x);
    f *= step(0.0, -coord.y);
    return abs(warp - f);
}

float getX(float i)
{
    float r = acos(i);
    return r;
}

// Heart curve suggested by IQ, improved by Dave_Hoskins.
float heart(vec2 p) {
    // Center it more, vertically:
    p.y += 0.6;
    // This offset reduces artifacts on the center vertical axis.
    const float offset = 0.3;
    // (x^2+(1.2*y-sqrt(abs(x)))^2âˆ’1)
    float k = 1.2 * p.y - sqrt(abs(p.x) + offset);
    float r = p.x * p.x + k * k - 1.0;
    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float asp = iResolution.x / iResolution.y;

    vec2 coord = uv * 2.0 - 1.0;

    vec2 aspCoord = vec2(coord.x * asp, coord.y);

    vec3 col = vec3(0.0);

    col += net(aspCoord) * 0.125;

    col += drawCoord(coord);
    aspCoord *= 1.5;

    //https://www.shadertoy.com/view/XsfGRn
    //Thanks to IQ
    float tt = mod(iTime, 1.5)/1.5;
    float ss = pow(tt, 0.2) * 0.5 + 0.5;
    ss = 1.0 + ss * 0.5 * sin(tt * 6.2831 * 3.0 + aspCoord.y * 0.5) * exp(-tt * 4.0);
    aspCoord *= vec2(0.5, 1.5) + ss * vec2(0.5,-0.5);

    col += vec3(abs(1e-1/heart(aspCoord)) * vec3(1.0, 0.2, 0.2));
    /*
    float time = iTime * 0.5;
    float animate = tan(time + abs(sin(time)));
    float f = clamp(animate - aspCoord.y, 0.0, 1.0);

    col += f * clamp(1.0 - heart(aspCoord), 0.0, 1.0) * vec3(1.0, 0.2, 0.2);
    */
    // Output to screen
    fragColor = vec4(col,1.0);
}
