// with the help of fabrice...

#define radial(p) atan(p.y, p.x)
#define discret(s, w) round(s / w) * w
#define wmod(s, w) mod(s + w / 2., w) - w / 2.
#define rotAbs(p, a) length(p) * cos(a + vec2(1.57, 0))
#define PI 3.1415
#define hue(v)  (.6 + .7 * cos((v) + vec4(0, 23, 21, 0)))

void mainImage(out vec4 o, vec2 u) {

    vec2 R = iResolution.xy,
         p = (u - R/2.) / R.y; 

    float nDiscs = 6., 
          slice = 2. * PI / nDiscs, 
          rad = radial(p)+ iTime,
          sid = discret(rad, slice),
          m = wmod(rad, slice);

    p = 1.5 * rotAbs(p, m);


    float id = p.y > .5 
                   ? discret(p.x, .2) * 10.
                   : sid;

    p.x = wmod(p.x, .2);
    p.y = abs(p.y - .5) - .08;

    o = mix(
            hue(sid) * .5,
            hue(id),
            smoothstep(4./R.y, .0, length(p) - .05)
        ) ;

}
