void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec4 col = texture(iChannel0,uv);

    // Output to screen
    fragColor =col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 colNew = texture(iChannel0,uv).rgb;
    vec3 colOld = texture(iChannel1,uv).rgb;


    vec3 diff = abs(colNew-colOld).rgb;
    vec3 col = step(vec3(0.2),diff) * colNew.xyz;
    fragColor=vec4(colNew,length(colNew-colOld));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 colNew = texture(iChannel0,uv);
    vec4 colOld = 0.7 * texture(iChannel1,uv);

    // Output to screen
    fragColor=clamp(vec4(colOld.w + colNew.w),0.,1.);
}
