#define PI 3.1415926538

float AngleBetween(in vec2 a, in vec2 b)
{
    return atan(b.y / b.x) - atan(a.y / a.x);
}

vec3 HueToRGB(in float hue)
{
    hue %= 360.0;

    float r = 0.0;
    float g = 0.0;
    float b = 0.0;

    if (hue <= 60.0 || hue >= 300.0)
        r = 1.0;
    else if (hue > 60.0 && hue < 120.0)
        r = abs(120.0 - hue) / 60.0;
    else if (hue < 300.0 && hue > 240.0)
        r = abs(240.0 - hue) / 60.0;

    if (hue >= 60.0 && hue <= 180.0)
        g = 1.0;   
    else if (hue > 180.0 && hue < 240.0)
        g = abs(240.0 - hue) / 60.0;     
    else if (hue < 60.0)
        g = abs(hue) / 60.0;

    if (hue >= 180.0 && hue <= 300.0)
        b = 1.0; 
    else if (hue > 120.0 && hue < 180.0)
        b = abs(120.0 - hue) / 60.0;     
    else if (hue > 300.0)
        b = abs(360.0 - hue) / 60.0;

    return vec3(r, g, b);
}

float RadToDeg(in float rad)
{
    return rad * 180.0 / PI;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
    float pixel = iResolution.y;
    float gap = iResolution.x - pixel;  

    fragCoord.x -= gap * 0.5;

    vec2 uv = fragCoord / pixel;

    uv -= vec2(0.5, 0.5);
    uv *= 2.0;

    if (uv.x > 0.0)
        uv.y *= -1.0;

    if (length(uv) < 0.6 || length(uv) > 1.0)
    {

        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
        return;
    }

    float angle = RadToDeg(AngleBetween(vec2(1.0, 0.0), uv));
    float a = (angle + 90.0) / 360.0;
    if (uv.x < 0.0)
        a = 1.0 - a;

    vec3 rgb = HueToRGB(a * 360.0 + iTime * 100.0);

    fragColor = vec4(rgb, 1.0);
}
