void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalcol = vec3(0.0);
    float time = iTime;

    for (float i = 0.0; i < 4.0; i++) {

        uv = fract(uv * 1.5) - 0.5;
        float d = length(uv) * exp(-length(uv0 * 1.5));
        float d0 = length(uv0);
        d = sin(d * (i + abs(cos(time))- 3. - time*0.5) + time) / 8.0;
        d = smoothstep(0.01 / d, 1.5, d);


        float colr = sin(time * 2.0)*0.3+(uv.x*sin(time)*0.1);
        float colg = 0.4/d0*0.1-(uv.y*sin(time)*0.1);
        float colb = cos(time*2.0)*cos(time)*(d0*sin(time)*0.2);
        if (i>=3.) colb =colr;
        vec3 col = vec3(colr, colg, colb);
        finalcol += col * (d + abs(cos(time) * 0.1));
    }

    fragColor = vec4(finalcol, 1.0);
}
