void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = (fragCoord*2.-iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;
    if(iMouse.z<.5){m.x = iTime*.2;
                    m.y = .3;}
    float w = 32.;//space resolution
    u = floor(u*w);
    float sum = 0.;
    for(float b=-w; b<=w; b++){
    for(float a=-w; a<=w; a++){
        float ang = 3.141592/w*(u.x*a + u.y*b) + m.x*11.*sqrt(a*a+b*b);
        sum += cos(ang)/exp((a*a+b*b)/w/w*m.y*33.);
    }}
    fragColor = vec4(sum/w*.2+.5);
}
