vec2 mainSound( int samp, float time )
{
    return vec2( float(expr(time))/128.-1. );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime*0.9765625*1.125;
    vec2 uv = fragCoord/iResolution.xy;
    uv.x += time/8.;
    uv.y += sin(iTime/4.);
    uv.x = mod(uv.x,1.);
    uv.y = mod(uv.y,1.);
    uv.x *= 2.-mod(time*(mod(time/16.,1.)>=.5?2.:1.),1.);
    fragColor = vec4(vec3(texture(iChannel0,uv).x),1.);
}
