// Value of pi
const float pi = 3.14159265359;

float sdStar5(in vec2 p, in float r, in float rf)
{
    const vec2 k1 = vec2(0.809016994375, -0.587785252292);
    const vec2 k2 = vec2(-k1.x,k1.y);
    p.x = abs(p.x);
    p -= 2.0*max(dot(k1,p),0.0)*k1;
    p -= 2.0*max(dot(k2,p),0.0)*k2;
    p.x = abs(p.x);
    p.y -= r;
    vec2 ba = rf*vec2(-k1.y,k1.x) - vec2(0,1);
    float h = clamp( dot(p,ba)/dot(ba,ba), 0.0, r );
    return length(p-ba*h) * sign(p.y*ba.x-p.x*ba.y);
}

float sdfCircle(vec2 uv, float r, vec2 c) {
    return length(uv - c) - r;
}

float sdfSquare(vec2 uv, float size, vec2 c) {
    float x = uv.x - c.x;
    float y = uv.y - c.y;
    return max(abs(x), abs(y)) - size;
}

vec3 drawScene(vec2 uv) {
    vec3 col = vec3(1);
    float piTime = mod(iTime, 2.*pi);

    //float revolution = piTime / (2.0*pi);

    // Smooth Step for aceleration
    float speed = smoothstep(0. , 2.*pi, piTime) * 2.*pi;

    vec2 path = vec2(0.35*cos(speed), 0.35*sin(speed));

    // Circles to make circle ring
    float circle = sdfCircle(uv, 0.3, vec2(0, 0));
    float circle2 = sdfCircle(uv, 0.4, vec2(0, 0));
    float res = max(-circle, circle2);

    // Draw res shape
    col = mix(vec3(1, 0, 0), col, step(0., res));

    // Draw star
    float star = sdStar5(uv + path, 0.035, 0.5);
    col = mix(vec3(1, 1, 0), col, step(0.01, star));
    // col = mix(vec3(1, 1, 0), col, smoothstep(0.01, star, 0.1));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Value of pi
    // const float pi = 3.14159265359;
    // float piTime = mod(2.0*pi, iTime);
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    vec3 col = drawScene(uv);
    fragColor = vec4(col,1.0);
}
