#define M(C,s) col = mix(col, C, clamp(s,0.,1.))

float circleSDF(vec2 uv, float s) {
    return length(uv) - s + 1.0;
}
float fill(float x, float s) {
    float pw = 1.0 / iResolution.y;
    return smoothstep(x - pw, x + pw, s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.0);
    col += (step(0.0, uv.x) - step(0.01, uv.x)) * vec3(1.0);

    vec3 r = vec3(1.0, 0.0, 0.0), g = vec3(0.0, 1.0, 0.0), b = vec3(0.0, 0.0, 1.0);
    vec3 c = vec3(0.0, 1.0, 1.0), m = vec3(1.0, 0.0, 1.0), y = vec3(1.0, 1.0, 0.0);

    if (iMouse.z >= 1.0) {
        r = c;
        g = y;
        b = m;

        c = vec3(1.0, 0.0, 0.0);
        m = vec3(0.0, 0.0, 1.0);
        y = vec3(0.0, 1.0, 0.0);

    }

    float s = 0.2, yO = 0.25, xO = 0.42;
    float leftACircle = fill(circleSDF(uv + vec2(xO, -yO * abs(sin(iTime))), s), 1.0);
    float leftBCircle = fill(circleSDF(uv + vec2(xO, yO * abs(sin(iTime))), s), 1.0);
    float rightACircle = fill(circleSDF(uv + vec2(-xO, -yO * abs(sin(iTime))), s), 1.0);
    float rightBCircle = fill(circleSDF(uv + vec2(-xO, yO * abs(sin(iTime))), s), 1.0);
    float midACircle = fill(circleSDF(uv + vec2(xO - 0.2 * sin(iTime), 0.0), s), 1.0);
    float midBCircle = fill(circleSDF(uv + vec2((xO - 0.2 * sin(-iTime)) - xO * 2.01, 0.0), s), 1.0);

    col += leftACircle * r + leftBCircle * b;
    col += rightACircle * c + rightBCircle * m;
    col += midACircle * g + midBCircle * y;

    col = M(c * m, rightACircle + rightBCircle - 1.0);
    col = M(y * c, midBCircle + rightACircle - 1.0);
    col = M(y * m, midBCircle + rightBCircle - 1.0);
    col = M(c * m * y, midBCircle + rightACircle + rightBCircle - 2.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
