#define round(x) floor((x) + 0.5)

const float PI = 3.141592653589793;

mat2 rotate2D(float r){
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}

vec3 hsv(float h, float s, float v){            // Head color  change
    vec4 t = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0*(1.+0.75*sin(iTime*0.2 )  ));
    vec3 p = abs(fract(vec3(h) + t.xyz) * 6.0 - vec3(t.w));
    return v * mix(vec3(t.x), clamp(p - vec3(t.x), 0.0, 1.0), s*0.9);
}

void twigl(out vec4 o, vec4 FC, vec2 r, float t) {
    o=vec4(0);
    float e=0.,g=0.,s;  
    for(float i=0.;i<1e2;i++){           // g scale factor 
                                               // vec2(x,y)offset //e; z axis position
        vec3 p=vec3((FC.xy-r*.5)/r.y*0.95 /**g */+vec2( 1.5*0.,0.00) ,g+=e);
        p.y-=t*.1;   // y axis move(up)
       //   p.x-=t*0.1 ; //p.z -=0.1*t*0. ; // x,z axis move

    //     p -=round(p) ; 
        p.y-=round(p.y);  //  p.x -=round(p.x) ;/* p.z -=round(p.z);*/
        p.zy *=rotate2D(iTime*PI*0.025) ;
        p.xy *=rotate2D(iTime*PI*0.015  );

        p.xz*=1.+sin(t*PI+(round(g)*0.+p.y)*9.)*.2;
        e=s=9.*0.5;
        for(int j=0;j<6;j++){
          //  e=s/2e6+min(e,max(-p.y,abs(length(p)-.2*1.3))/s),p.y+=length(p.xz)*2.,p=.2-abs(p*3.);
           e=s/2e6+min(e*0.95,max(-p.y+0.035,abs(length(p)-.2*1.3))/s) ,
             p.y+=length(p.xz)*2.*.75,
           p=.2-abs(p*3.*0.85-vec3(0,2e-3/dot(p.xz,p.xz),0)*0.5  /**(1.+0.15*abs(sin(iTime*0.15))  ) */);

           s+=s;
        }
        o.rgb+=hsv(.6+.9/p.y,.9,4e-6/e);
    }
}

