void mainImage(out vec4 f, in vec2 c){
    vec2 u=(2.*c-iResolution.xy)/min(iResolution.y,iResolution.x);
    vec2 v=u,z=vec2(.6),x=vec2(2e3,1.),g;
    float i=0.;
    for(float j=0.;j<x.x;j++){
        z+=vec2(sin(z.y),cos(z.x))+v;
        z+=vec2(cos(z.y),sin(z.x))+v;
        if(length(z)>500.){i=j;break;}
    }
    g=vec2(i/10./length(z)*10.,0.);
    vec3 b=vec3(.5+.5*cos(g.x*6.2),.5+.5*sin(g.x*6.2),.5-.5*cos(g.x*6.2));
    i<300.?(b*=vec3(.1*sin(g.x*6.2)),b+=mix(vec3(.6,.2,.6)*.1,b,.1)):(b+=vec3(.2,.2,.6));
    i<1.?(g.x=length(z),b=vec3(.1*sin(g.x*6.2))):b;
    f=vec4(b,1.);
}
