// You can use this shader as a template for ray marching shaders

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
#define TAU 6.283185
#define PI 3.141592
#define S smoothstep
#define T iTime

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}
float SDFsphere(vec3 point, float radius)
{
    return length(point) - radius;
}
float SDFTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float SDFRotateTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xy)-t.x,p.z);
  return length(q)-t.y;
}
float smoothMax(float a, float b, float k)
{
    return log(exp(k*a) + exp(k*b))/k;
}

float smoothMin(float a, float b , float k)
{
    return -smoothMax(-a, -b, k);
}


float sdMug(vec3 point, float offset) {
    float RADIUS = 0.975f;
    vec2 INNERCIRCLE = vec2(1.0f,0.05f);
    float d = 0.0;
    point += vec3(offset,-0.5,0.0);
    float sphere = SDFsphere(point+vec3(0.0,0.75,0.0), RADIUS );
    float torus = SDFTorus(point, INNERCIRCLE- vec2(0.0,0.05));
    float torusMouth = SDFTorus(point+vec3(0.0,0.15,0.0), INNERCIRCLE*1.05);
    float sphereBool = SDFsphere(point+vec3(0.0,0.35,0.0), RADIUS*1.00);
    float rotateTorus = SDFRotateTorus(point + vec3(1.15,0.85,0.0), 0.35*(INNERCIRCLE + vec2(0.0,0.2) ));
    d = smoothMin(sphere, torus, 3.5f);
    d = smoothMin(rotateTorus,d,9.5f);
    d = max(d,-sphereBool);
    d = min(d, torusMouth);
    return d;
}

vec4 unionSDF(vec4 a, vec4 b) {
    return a.w < b.w? a : b;
}

vec2 GetDist(vec3 p) {
    float d = sdMug(p, -3.0);
    float f = sdMug(p, 2.0);
    if (f < d){
        return vec2(f,1.0);
    } else {
        return vec2(d,0.0);
    }
}

vec2 RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;
    float mug = 0.0;
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        vec2 dist = GetDist(p);
        float dS = dist.x;
        mug = dist.y;
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return vec2(dO, mug);
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(.001, 0);
    vec3 n = GetDist(p).x - 
        vec3(GetDist(p-e.xyy).x, GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 
        f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u;
    return normalize(i);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = vec2(0.1, 0.1)/iResolution.xy;

    vec3 ro = vec3(-1, 8, 1);
    ro.yz *= Rot(-m.y*PI+1.);
    ro.xz *= Rot(-m.x*TAU);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0), 1.);
    vec3 col = vec3(0.25, 0.25, 0.25);

    vec2 d = RayMarch(ro, rd);

    if(d.x<MAX_DIST) {
        vec3 p = ro + rd * d.x ;

        vec3 n = GetNormal(p);

        vec3 r = reflect(rd, n);

        float dif = dot(n, normalize(vec3(mod(iTime, 3.0)*10.0,2,5)))*.7+.5;

        col = vec3(0., dif-0.5, 0.);
        if (d.y == 1.0){
            col = vec3(dif, 0., 0.);
        }

    }

    col = pow(col, vec3(.5));


    fragColor = vec4(col,1.0);
}
