void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
        vec2 xy = fragCoord.xy; //current coords
        vec2 goob = fragCoord.xy / iResolution.xy;
        float halfx = iResolution.x/2.0;
        float halfy = iResolution.y/2.0;
        float x, y ,z;

        vec4 texColor = texture(iChannel0,goob);
        if(xy.x < halfx)
        {
        x = 0.1*cos(iTime);
        y = 0.1*cos(iTime);
        z = 0.1*cos(iTime);

        }
        else
        {
         x = 0.5*cos(iTime*.3);
         y = 0.5*cos(iTime*20.);
         z = 0.5*cos(iTime*.126);
        }

        if(xy.y < halfy)
        {
         x = 0.5*cos(iTime*100.0);
         y = 0.5*cos(iTime*200.0);
         z = 0.5*cos(iTime);
        }
        else
        {
         x = 0.5*cos(iTime*.6);
         y = 0.5*cos(iTime*0.25);
         z = 0.5*cos(iTime*.2);
        }
        if(xy.y > halfy && xy.x > halfx) //top right
        {
         x = 0.5*tan(iTime*.675);
         y = 0.5*tan(iTime*.707);
         z = 0.5*tan(iTime*2.);
        }
        highp int k = int(xy.x);
        highp int j = int(xy.y);
        highp int timeInt = int(iFrameRate);
        highp int timey = int(iTime);
        if(timey%2 ==0)
        {
        if (k %j ==0) //CRT TEXTURE
        {
            x *=0.;
            y*=2.;
            z*=0.;
        }
        if (j%k ==0) //CRT TEXTURE
        {
            x *=10.;
            y*=10.;
            z*=10.;
        }
        if (k %timeInt ==0) //CRT TEXTURE
        {
            x *=0.;
            y*=0.;
            z*=0.;
        }
        }
        if(k%2 == 0)
        {
            x/=2.;
            y/=2.;
            z/=2.;
        }
        fragColor = vec4(texColor.x+x,y,texColor.z+z,1);
}
