#define A 0.01
#define R 0.05
#define PI 3.14151
#define TAU (2.0 * PI)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 aspect = normalize(iResolution.xy);
    vec2 pos = R * vec2(cos(iTime), sin(iTime));

    float d = length(((0.5-fragCoord/iResolution.xy)*aspect - pos));
    float brightness = A/d;
    vec3 tint = normalize(0.5+0.5*sin(vec3(iTime*2.0, iTime*3.0, iTime*5.0)));

    fragColor = vec4(0.0);

    pos = 0.5 - fragCoord / iResolution.xy;
    float dist = length(pos * aspect);
    float r = 0.2+texture(iChannel0, vec2(atan(pos.x/pos.y) * (pos.y > 0.0 ? -1.0 : 1.0) / PI * 0.61 + 1.0 / PI, 0.2)).x;
    if(abs(dist - r*0.2) < 0.01) {
        fragColor += vec4(1.0);
    }


    fragColor += vec4(brightness*tint,1.0);
}
