#define R(p,a,t) mix(a*dot(p,a),p,cos(t))+sin(t)*cross(p,a)
#define H(h) (cos((h)*6.3+vec3(0,23,21))*.5+.5)

vec3 palette1( in float t)
{
    vec3 a = vec3(0.648, 0.828, 0.738);
    vec3 b = vec3(0.448, 0.228, 0.268);
    vec3 c = vec3(3.138, 2.488, 1.858);
    vec3 d = vec3(1.078, -0.132, 0.358); 

    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 palette2( in float t)
{   
    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.5,0.5,0.5);
    vec3 c = vec3(1.0,1.0,1.0);
    vec3 d = vec3(0.0,0.33,0.67) ;

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage(out vec4 O, vec2 C)
{
    vec3 p,r=iResolution,c=vec3(0),
    d=normalize(vec3((C-.5*r.xy)/r.y,0.6));                       // change zoomF 1=> 0.6
 	for(float i=0.,s,e,g=0.,t=iTime;i++<80.;){
        p=g*d;
        p.z-=.4;
        p=R(p,H(t*.03), t*.032);
        p.x-=.1;
        p=(sin(abs(p-0.00)));   // change -0.0125 offset
        s=1.5;
        for(int j=0;j++<8;)
           s*=e=max(.5/dot(p,p),2.3*0.451*(1.+0.2*sin(iTime*0.2 )  )), // change *0.451*( )
           p=abs(p*e-vec3(2,3,2)*0.58),                                 // change *0.8
           p=R(p,normalize(vec3(3,3,1)),1.8);
        g+=e=abs(p.z)/(s  )+1e-4*0.5;                                // change *0.5

       //   default color case1    // +5.*sin( ) ad
        c+=mix(vec3(1),H((log(s)+5.*sin(iTime*0.2 ))*.4) ,.25)*.03/exp(1.8*i*i*e);        //  change 0.25=>0.5
       //   palette() color case2
      //  c+=mix(palette1(s+2.*sin(iTime*0.3)),palette2((s)+1.*sin( iTime*0.2 ))  ,.15)*.03/exp(1.8*i*i*e);
}                            

	c*=c*c*c;
    O=vec4(c,1);
}

