void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float minres = min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord - .5*iResolution.xy) / minres;

    uv *= 1.5;
    vec3 col;

    float c = iTime*2.+1111.;

    col += exp(-pow(length(uv),3.)*11.)*(3.*sin(3.*sin(sin(c+4.*sin(uv.x*.00011*c+sin(sin(2.*sin(sin(sin(sin(sin(.001*c*sin(sin(sin(sin(sin(uv.x*22.)))))))))))))))));



    fragColor = vec4(col/2.4,1.0);
}
