void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(texture(iChannel0, uv));

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = floor(fragCoord / 40.0);

    int living = 0;
    living += int(texelFetch(iChannel0, ivec2(fragCoord) + ivec2(1, 1 ), 0));
    living += int(texelFetch(iChannel0, ivec2(fragCoord) + ivec2(1, 0 ), 0 ));
    living += int(texelFetch(iChannel0, ivec2(fragCoord) +ivec2(1, -1), 0));
    living += int(texelFetch(iChannel0, ivec2(fragCoord) +ivec2(0, -1), 0));
    living += int(texelFetch(iChannel0, ivec2(fragCoord) +ivec2(0, 1 ), 0));
    living += int(texelFetch(iChannel0, ivec2(fragCoord) +ivec2(-1, -1), 0));
    living += int(texelFetch(iChannel0, ivec2(fragCoord) +ivec2(-1, 0 ), 0));
    living += int(texelFetch(iChannel0, ivec2(fragCoord) +ivec2(-1, 1 ), 0));



    bool live = false;

    switch (living)
    {
        case 2:
            live = bool(texelFetch(iChannel0, ivec2(fragCoord), 0 ));
            break;
        case 3:
            live = true;
            break;
        default:
            live = false;
    }

    fragColor = vec4( vec3(live), 1.0);

        if(iFrame < 4)
    {
        fragColor = vec4(vec3(gold_noise(fragCoord, iTime) > 0.95),1.0);
        return;
    }

}

float PHI = 1.61803398874989484820459;  // Î¦ = Golden Ratio   

float gold_noise(in vec2 xy, in float seed){
       return fract(tan(distance(xy*PHI, xy)*seed)*xy.x);
}
