float sq(float x) {
    return x*x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xy*0.4;
    vec3 col;
    for(float j = 0.0; j < 3.0; j++){
        for(float i = 1.0; i < 20.0; i++){
            p.x += 0.1 / (i + j) * cos(i * 10.0 * p.y + iTime + sin((iTime / (1. * i + j)) * i + j));
            p.y += 0.1 / (i + j)* cos(i * 10.0 * p.x + iTime + sin((iTime / (1. * i)) * i + j));
        }
        col[int(j)] = sin(.5 * 7.0*sq(p.x)) + sin(7.0*sq(p.y));
    }
    fragColor = vec4(col, 1.);
}
