// Noise algorithms source:
// https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83

float rand(float n){return fract(sin(n) * 43758.5453123);}

float noise(float p){
	float fl = floor(p);
    float fc = fract(p);
	return mix(rand(fl), rand(fl + 1.0), fc);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    vec2 shift = vec2(noise(iTime * 5.0) * 0.02, noise(iTime * 0.5) * 0.02);

    vec4 colR = texture(iChannel0, uv - shift);
    vec4 colG = texture(iChannel0, uv);
    vec4 colB = texture(iChannel0, uv + shift);

    fragColor = vec4(colR.r, colG.g, colB.b, 1.0);
}
