// https://www.shadertoy.com/view/DlcyWs alternating domino tiling, 2023 by jt

// https://www.shadertoy.com/view/WttXWX "Best" Integer Hash by FabriceNeyret2,
// implementing Chris Wellons https://nullprogram.com/blog/2018/07/31/
int triple32(int x)
{
    x ^= x >> 17;
    x *= 0xed5ad4bb;
    x ^= x >> 11;
    x *= 0xac4c1b51;
    x ^= x >> 15;
    x *= 0x31848bab;
    x ^= x >> 14;
    return x;
}

#define HASH(u) triple32(u)

int ihash(ivec2 v)
{
    //v+=ivec2(iMouse);
    return HASH(v.x + HASH(v.y));
}

bool bhash(ivec2 v)
{
    return (ihash(v) & 1) != 0;
}

ivec3 random_domino_combined_squares(ivec2 position) // https://www.shadertoy.com/view/mlGcWR random domino square tiling int (jt)
{
    ivec2 quadrant = position & 1; // [0,0] to [1,1]
    ivec2 tile = position - quadrant; // round down to even number

    bool flip = bhash(tile);
    if(flip)
    {
        if(bhash(tile + 2 * ivec2(quadrant.x * 2 - 1, 0))) // neighbor tile aligned?
        {
            tile += ivec2(quadrant.x * 2 - 1, 0); // adjust index to merged tile
            return ivec3(tile,+1);
        }
    }
    else
    {
        if(!bhash(tile + 2 * ivec2(0, quadrant.y * 2 - 1))) // neighbor tile aligned?
        {
            tile += ivec2(0, quadrant.y * 2 - 1); // adjust index to merged tile
            return ivec3(tile,-1);
        }
    }

    return ivec3(vec2(0), 0);
}

bool bhash2(ivec2 v)
{
    return bhash(random_domino_combined_squares(v/4).xy);
}

ivec3 random_domino_combined_squares2(ivec2 position) // https://www.shadertoy.com/view/mlGcWR random domino square tiling int (jt)
{
    ivec2 quadrant = position & 1; // [0,0] to [1,1]
    ivec2 tile = position - quadrant; // round down to even number

    bool flip = bhash2(tile);
    if(flip)
    {
        if(bhash2(tile + 2 * ivec2(quadrant.x * 2 - 1, 0))) // neighbor tile aligned?
        {
            tile += ivec2(quadrant.x * 2 - 1, 0); // adjust index to merged tile
            quadrant.x ^= 1; // horizontal merge: flip horizontal quadrant
            return ivec3(tile,+1);
        }
    }
    else
    {
        if(!bhash2(tile + 2 * ivec2(0, quadrant.y * 2 - 1))) // neighbor tile aligned?
        {
            tile += ivec2(0, quadrant.y * 2 - 1); // adjust index to merged tile
            quadrant.y ^= 1; // vertical merge: flip vertical quadrant
            return ivec3(tile,-1);
        }
    }

    return ivec3(tile, 0);
}

bool edge(vec2 I)
{
    float e = 0.1;
    ivec2 a = random_domino_combined_squares2(ivec2(floor(I-e))).xy;
    ivec2 b = random_domino_combined_squares2(ivec2(floor(I+e))).xy;
    return any(not(equal(a, b)));
}

vec3 hash32(vec2 p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

void mainImage(out vec4 o, in vec2 I)
{
    vec2 R = iResolution.xy;
    I = (2. * I - R) / R.y;
    vec3 color = vec3(0);
    if(all(lessThan(abs(I), vec2(1))))
    {
        //I += iMouse.xy/10.0; // explore pattern
        I *= 50.0;
        ivec2 position = ivec2(floor(I));
        ivec3 result = random_domino_combined_squares2(position);
        color = hash32(vec2(result.xy));
        if(iMouse.z > 0.0)
        {
            if(result.z == 0) color *= 0.0;
            if(result.z > 0) color *= 0.5;
        }
        else
        {
            if(result.z > 0) color = mix(color, vec3(1,0.8,1), 0.5);
            if(result.z < 0) color = mix(color, vec3(0,0.5,0), 0.5);
            if(result.z == 0) color = dot(color, vec3(1.0)) * vec3(0.1);
        }
        if(edge(I)) color *= 0.0;
    }
    //color = sqrt(color); // gamma
    o = vec4(color, 1);
}
