void mainImage(out vec4 O, vec2 I)
{
    vec2 uv = I / iResolution.xy;

    // Create pattern based on UV coords and time
    vec3 color = vec3(mod(uv.x * uv.y * 10.0 + iTime * 2.0, 1.0));

    // Add animated movement
    vec2 movement = vec2(sin(iTime), cos(iTime));
    uv += movement * 0.1;

    // Apply gradient based on modified UV coords
    color *= vec3(uv, 1.0);

    O = vec4(color, 1.0);
}
