void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 draw = texture(iChannel0,uv);
    vec3 col = draw.rgb;
    /*if(length(iMouse.xy-fragCoord) < 10.0)
    {
        draw = vec4(1.0);
    }*/

    //col = mix (col, vec3(0.0,0.0,1.0), 1.0-smoothstep(0.03,0.035, length(fragCoord-abs(iMouse.xy))));
    col = mix (col, vec3(1.), smoothstep(30.,1., length(fragCoord-abs(iMouse.xy))));
    col = clamp(col, 0., 1.);

    fragColor = vec4(vec3(-.01, -.01, -.005),1.0) + vec4(col, 1.0);
}
