vec2 dft(float y, vec2 v)
{
    float c = v.x;
    float s = v.y;
    float f = pow(2.,5.*y)*10.;
    for (int i = 0; i< 512; i++){
        float x = float(i)/512.;
        s += (texture(iChannel0, vec2(x,1.)).x-.5)*sin(x*f);
        c += (texture(iChannel0, vec2(x,1.)).x-.5)*cos(x*f);    
    }
    return vec2(s,c);


}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixelSize = 1. / iResolution.xy;
    vec2 uv = fragCoord.xy * pixelSize;

    if(uv.x > 1. - pixelSize.x){

        vec2 v = texture(iChannel0, uv* vec2(pixelSize.x,0)).xy*2.-1.;
        vec2 d = dft(uv.y,v);
        fragColor = vec4(d.x*.5+.5,d.y*.5+.5,1.,0.)*length(d); 

    }else{
        fragColor = texture(iChannel1, uv + vec2(1,0) * pixelSize);
    }
}
