vec3 sqr(vec3 x)
{
    return x * x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 red = vec3(1,0,0);
    vec3 green = vec3(0,1,0);

    vec3 mixl = mix(red, green, uv.x);
    vec3 mix2 = sqrt(mix(sqr(red), sqr(green), uv.x));  // <- gamma correct; shows on top

    vec3 col = mix(mixl, mix2, (uv.y < 0.5) ? 0.0 : 1.0);


    // Output to screen
    fragColor = vec4(col,1.0);
}
