const float FROST_INTENSITY = .1;

vec3 tex(vec2 uv) {
    return pow(texture(iChannel0, uv).rgb, vec3(2.2));
}

float ring_dist(vec2 uv, float radius) {
    return abs(length(uv) - radius);
}

// https://www.shadertoy.com/view/4djSRW
vec2 hash22(vec2 uv) {
	vec3 p3 = fract(vec3(uv.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx + p3.yz) * p3.zy);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = fragCoord / iResolution.xy;
    vec2 uv = (fragCoord - .5 * iResolution.xy) * 2. / iResolution.y;
    float t = iTime;

    float d = length(uv);

    float ring = clamp(abs(1. - .01 / ring_dist(uv, .75)), 0., 1.);
    vec2 noise = (hash22(st * 1000.) * 2. - 1.) * ring * FROST_INTENSITY;
    vec2 tex_offset = vec2(t / 4., sin(t / 8.));
    vec3 color = tex(st + noise + tex_offset);

    vec3 srgb = pow(color, vec3(1. / 2.2));
    fragColor = vec4(srgb, 1.0);
}
