vec2 CPow(vec2 a, float P){
    float theta = atan(a.y,a.x);
    float r = length(a);
    r = pow(r,P);
    theta *= P;
    return r * vec2(cos(theta),sin(theta));
}
vec2 CDiv(vec2 a,vec2 b){
    float r = length(a);
    float theta = atan(a.y,a.x);
    float r2 = length(b);
    float theta2 = atan(b.y,b.x);
    r /= r2;
    theta -= theta2;
    return r * vec2(cos(theta),sin(theta));

}
vec2 CMul(vec2 a,vec2 b){
    float r = length(a);
    float theta = atan(a.y,a.x);
    float r2 = length(b);
    float theta2 = atan(b.y,b.x);
    r *= r2;
    theta += theta2;
    return r * vec2(cos(theta),sin(theta));

}
vec2 IMPow(vec2 z, vec2 P){
    vec2 real =CPow(z,P.x);
    vec2 Imag = vec2(cos(log(z.x + z.y) * P.y),sin(log(z.x + z.y) * P.y));
    return CMul(real,Imag);
}
vec2 Zeta(vec2 z){
    vec2 v= vec2(0,0);
    for(float n = 1.;n<300.;n++){
        v += CDiv(vec2(1,0),IMPow(vec2(n,0),z));
    }
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){


    vec2 uv = fragCoord/iResolution.xy - .5;
    uv.x *= iResolution.x/iResolution.y;
    uv *=10.;
    vec3 col;
    vec2 z = Zeta(vec2(.5,3. * iTime));
    col += .1/length(uv - z);
    col += .1/length(uv);


    // Output to screen
    fragColor = vec4(col,1.0);
}
