#define PI     3.14159265
#define TWO_PI 6.28318530

// Smooth HSV to RGB conversion 
vec3 hsv2rgb_smooth( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 center = vec2(0.5,0.5);

    vec2 ball1 = vec2(cos(PI / 2.0) * 0.2 + 0.5, sin(PI / 2.0) * 0.2 + 0.5);
    vec2 ball2 = vec2(cos(7.0 * PI / 6.0) * 0.2 + 0.5, sin(7.0 * PI / 6.0) * 0.2 + 0.5);
    vec2 ball3 = vec2(cos(11.0 * PI / 6.0) * 0.2 + 0.5, sin(11.0 * PI / 6.0) * 0.2 + 0.5);

    float dist = length(center - uv);
    float dist1 = length(ball1 - uv);
    float dist2 = length(ball2 - uv);
    float dist3 = length(ball3 - uv);

    vec3 col = vec3(0.5,0.5,0.5);

    if (dist1 < 0.125)
    {
        if (dist < 0.2)
            col = hsv2rgb_smooth(vec3(dist * 2.0 + iTime, 1.0,1.0));
        else
            col = vec3(0.203125,0.25,0.33203125);
    }
    if (dist2 < 0.125)
    {
