float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

float circle(vec2 uv, float r) {

    return length(uv) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - iResolution.xy*0.5)/iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0.1, 0.1, 0.2);

    float delta1 =  -2. * iTime;
    float n = 0.1 * noise(uv * 100. + delta1);
    float d = abs(circle(uv + n * 0.1, 0.3));
    float circle1 = 0.01/(d + n * n * 0.1) - 0.02;
    col = mix(col, vec3(0.1, 0.4, 0.8), circle1);

    float delta2 = - 0.9 * 2.*iTime;
    float n2 = 0.1 * noise(uv * 20. + delta2);
    float d2 = abs(circle(uv + n2 * 0.1, 0.2));
    float circle2 = 0.003/(d2 + n2 * n2 * 0.1) - 0.006;
    col = mix(col, vec3(0.8, 0.5, 0.1), circle2);

    float delta3 = - 2.*iTime;
    float n3 = 0.1 * noise(uv * 20. + delta3);
    float d3 = abs(circle(uv + n3 * 0.1, 0.4));
    float circle3 = 0.003/(d3 + n3 * n3 * 0.2) - 0.006;
    col = mix(col, vec3(0.5, 0.3, 0.5), circle3);


    float delta4 = delta1 + delta2 + delta3;
    float n4 = 0.1 * noise(uv * 30. + delta4);
    float d4 = circle(uv + n4 * 0.1, 0.1);
    float circle4 = 0.005/ d4 + 0.02;
    col = mix(col, vec3(0.7, 0., 0.8), circle4);


    col = abs(col/2.);

    // Output to screen
    fragColor = vec4(col,1.0);
}
