float Snow(vec2 uv, float m){
    float c = fract(sin(uv.x * 2321. - uv.y * m));
    return c; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -.5 * iResolution.x)/iResolution.y;

    float t = iTime;

    float m = cos(t * 8824.) * 4352.;

    float d = Snow(uv, m);

    fragColor = vec4(vec3(d) ,1.0);
}
