void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float distortMag = 0.05;

    // Output to screen
    float distortC = distortMag * cos(iTime);
    float distortS = distortMag * sin(iTime);

    vec4 rChannel = texture(iChannel0,vec2(uv.x + distortC, uv.y + distortS));
    vec4 gChannel = texture(iChannel0,vec2(uv.x - distortC, uv.y - distortS));
    vec4 bChannel = texture(iChannel0,vec2(uv.x - distortC, uv.y + distortS));


    fragColor = vec4(rChannel.x,gChannel.y,bChannel.z,1);
}

