    vec2 headPos = vec2(0.5, 0.28);
    vec4 headCol = vec4(1,1,1,1);
    float headSize = 0.25;

    //Eyes
    vec2 lEyePos = vec2(0.6, 0.31);
    vec2 rEyePos = vec2(0.4, 0.31);
    vec4 eyeCol1 = vec4(1,0,0,1);
    vec4 eyeCol2 = vec4(1,1,1,1);
    float eyeSize = 0.05;
    float eyeDis = 0.6;

    fragColor = vec4(0,0,0,1);

    if (distance(uv,headPos) < headSize)
    {
        fragColor = headCol * sin(uv.y-headPos.y) - abs(uv.x-headPos.x) + 0.3;
    }
    if (distance(uv,lEyePos) < max(eyeSize/4.f, eyeSize * sin(iTime)))
    {
