mat2 Rotate(float a) {
  float s = sin(a);
  float c = cos(a);
  return mat2(c, -s, s, c);
}

vec4 doFractalEffect(vec4 fragCoord,vec2 iResolution, float time) {
    vec4 color;
    float e,g,v, s = 2.,t = time+sin(time);

     for(float i=0.;i<50.;i++){
        v=2.;
        vec3 point = vec3((fragCoord.xy*v-iResolution.xy)/iResolution.y*g,g);
        point.yz *= Rotate(.1 + iTime*0.1);
        point.y -= .45 + sin(t)*.01;
        point.xy *= Rotate(.1);

        for(int j ; j++<5 ; point=abs(point)/e - vec3(.3,3,0))
            point.xz = length(point.xz) * vec2(cos(e=fract(atan(point.x,point.z)*.7+(j<2?t/3.:s))-.5),e),
            v /= e = clamp(dot(point,point),.1,.52);

        g += e = point.x/v;
        color += .02 / exp(e* 30000.);
    }

    return vec4(vec3(color.x + e*522., color.y+g, color.z + g*3.),1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = doFractalEffect(vec4(gl_FragCoord), iResolution.xy, iTime);
}
