void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    fragColor=vec4(1.0,1.0,1.0,1.0);

    if (uv.y<0.6667+cos(iTime) && uv.y>0.3333+cos(iTime) && uv.x>0.3333 && uv.x<0.6667) fragColor=vec4(0.8,0.9,0.0,1.0);

    if ((uv.x-0.7+cos(iTime))*(uv.x-0.7+cos(iTime))+(uv.y-0.3+cos(iTime))*(uv.y-0.3+cos(iTime))<=0.025) fragColor=vec4(0.9,0.0,0.75,1.0);

    if(uv.y>0.5*uv.x+0.1 && uv.y<(-uv.x)+0.9 && uv.x>0.2) fragColor=vec4(0.4,0.4,0.4,1.0);


}
