void mainImage(out vec4 c, in vec2 i) {
  vec2 r = iResolution.xy/3.;
  i = mod(i,r);
  float t = iTime;
  vec2 n = (i/r.y-vec2(r.x/r.y,1.)*0.5)*3.;
  vec2 p = vec2(sqrt(pow(n.x,2.)+pow(n.y,2.)), atan(n.y/n.x));
  float a = p.y*cos(t/1.4);
  float d = cos(t/1.3)*.1/pow(1.-p.x,2.);
  c = vec4(d+sin(t/1.5)-cos(t/1.6),d+2.*a*sin(t),d-a*cos(t/1.2),1.);
}
