void mainImage(out vec4 O, vec2 F) {
    vec2 r = iResolution.xy;
    float i = .34, l = length(F+=F-r)/r.y + i, t = iTime;

    for (O *= 0.; i < 30.; 
         O += length(min(r.y/abs(F),r))/3e2*(cos(++t+i+vec4(4,7,8,7))*l+l)) 
         F *= mat2(cos(l*.2-i++*--t/1e2+vec4(0,45,12,0)));
}
