void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    //uv = abs(uv);

    uv = floor(uv * 30.);

    uv = abs(uv);

    uv = uv.yx;


    uv.x += sin((uv.y + iTime) * 3. + iTime * 2.);
    uv.y += cos((uv.x + iTime) * 3. + iTime * 2.);

    float c = step(uv.x,.05);

    float d = length(uv);
    float r = 0.3;

    float blur = 0.2;
    d = smoothstep(r - blur,r,d * c);


    // Output to screen
    fragColor = vec4(vec3(d),1.0);
}
