mat2 rotate2D(float rad){
    return mat2(cos(rad), sin(rad), -sin(rad), cos(rad));
}

void mainImage( out vec4 o, in vec2 FC )
{
float d=.1,e;vec3 p;
o=vec4(0.);

for(float i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e)*2.;
    p.z-=4.;//p.y-=1.;
    p.xz*=rotate2D(mod(iTime*.1,.7));

    for(float j=0.;j<8.;j++){   
        p=vec3(p.x*p.x-p.y*p.y,2.*p.x*p.y,1);
        d=length(p-.1)-.1;
        d-=dot(p,p)/2e4;

    }e+=d;
}o=vec4(p.xyy,1);
}
