#define PI 3.14159265359
#define PI2 (PI*2.)


float n(vec2 uv)
{
    return texture(iChannel0, uv).r;
}

float b(vec2 uv, vec2 b )
{
    vec2 d = abs(uv)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

vec3 c(vec2 uv)
{
    float s = 100.0;

    vec2 or = uv;
    float id = ceil(length(uv)*s)/s;

    float r1 = n(vec2(id*13.31231542));
    float r2 = n(vec2(id*380.49643332));
    float r3 = n(vec2(id*43149.24309));
    float r4 = n(vec2(id*859.3003850));


    float a = iTime * max(0.5, r1 * 15.)  + r2;

    uv *= mat2(cos(a), sin(a), -sin(a), cos(a));
    uv = vec2( atan(uv.x,uv.y) / PI2 + 0.5, length(uv));

    uv.y = fract(uv.y * s) - 0.5;
    uv.x -= 0.5;
    uv.x *= 5.;

    float t = b(uv, vec2(mix(1.1, 1.8, r4) / max(id * 7., 1.), 0.1)) -0.05;
    t = 1.-smoothstep(max(0.001, t), 0.0, 0.05);

    return vec3(t * r1, t * r2, t * r3);// + vec3(uv, 0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    float o = 0.002;

    float r = c(uv+o).r;
    float g = c(uv).g;
    float b = c(uv-o).b;


    vec3 col = vec3(r,g,b)*3.;

    fragColor = vec4(col,1.0);
}
