#define R iResolution
#define T iTime

const float pi = 355. / 113.;
const float halfpi = 0.5 * pi;

float psin (float t) {
    return 0.5 * (1. + sin (pi * t - halfpi));
}
float pcos (float t) {
    return 0.5 * (1. + sin (pi * t));
}

void mainImage (out vec4 C, in vec2 F) {
    float t = T / 10.;
    vec2 uv = F / R.xy;
    vec2 uvn = (F - 0.5 * R.xy)
        / max (R.x, R.y)
        ;

    float s = texture (iChannel0, uvn).r;
    s = smoothstep (-psin (t), pcos (t), s);

    float t_tx = 0. * t;
    vec2 uv_tx = 0.02*vec2 (sin (t_tx), cos (t_tx)) + uvn;
    vec3 c = texture (iChannel1, s * 2. * uv_tx).rgb;

    C = vec4(c, 1.0);
}
