#define R iResolution.xy
#define T (iTime)
#define M_PI (3.1416)
#define TAU (M_PI * 2.0)


void mainImage( out vec4 O, in vec2 fc )
{
    vec3 col = vec3(0.0);
    vec2 uv = (fc-0.5*R.xy)/R.y;


    uv = abs(uv);
    uv += vec2(cos(T*0.5), sin(T*0.5))*0.5;

    float x = cos(uv.y*3.21);
    float y = sin(uv.x*3.291);
    uv += vec2(x, y);

    vec3 a = texture(iChannel0, uv).rgb;
    vec3 b = texture(iChannel1, (uv-0.1)*((a.xy+a.z))).rgb;
    vec3 c = texture(iChannel2, (-uv)*((b.xy+b.z))).rgb;
    vec3 d = texture(iChannel2, (uv*0.5)*((c.xy+c.z))).rgb;

    col = (a+b+c+d)/4.0;

    col *= (b-(d*0.5));


    col /= vec3(1.0)+col;
    col = pow(col, vec3(0.588125));


    O = vec4(col, 1.0);
}

const float freqs[4] = float[4](130.8, 174.6, 185.0, 196.0);

vec2 mainSound( int samp, float time )
{
    float L = 0.0;
    float R = 0.0;
    float t = fract(time);
    int idx = int(t*4.0)%4;
    float id = floor(time);
    float other = float(int(id) % (1+idx) == 0);
    float note = freqs[idx];
    note *= 12.0;
    float tone = sin(note*t);
    float harmonic = cos((note/12.0)*fract(time*0.01));
    float sub = tan(note*fract(time*0.5))*other;
    tone += harmonic;
    tone += sub;
    L = tone;
    R = tone;
    return vec2(L, R);
}
